{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = spawn_spawn_domain_call__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).spawn_domain_call).path = ((char *)(path));
    ((_binding->tx_union).spawn_domain_call).argvbuf = ((char *)(argvbuf));
    ((_binding->tx_union).spawn_domain_call).argvbytes = argvbytes;
    ((_binding->tx_union).spawn_domain_call).envbuf = ((char *)(envbuf));
    ((_binding->tx_union).spawn_domain_call).envbytes = envbytes;
    FL_DEBUG("lmp TX spawn.spawn_domain_call\n");
    
    // try to send!
    spawn_spawn_domain_call__lmp_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

