/*
 * Cap send/receive handlers
 */
static  void spawn_ump_cap_rx_handler(void *arg, errval_t success, struct capref cap, uint32_t capid)
{
    // Get the binding state from our argument pointer
    struct spawn_binding *_binding = arg;
    struct spawn_ump_binding *b = arg;
    errval_t err;
    err = SYS_ERR_OK;
    
    assert(capid == (((b->ump_state).capst).rx_capnum));
    
    // Check if there's an associated error
    // FIXME: how should we report this to the user? at present we just deliver a NULL capref
    if (err_is_fail(success)) {
        DEBUG_ERR(err, "error in cap transfer");
    }
    
    // Switch on current incoming message
    switch (_binding->rx_msgnum) {
    case spawn_spawn_domain_with_caps_call__msgnum:
        // Switch on current incoming cap
        switch ((((b->ump_state).capst).rx_capnum)++) {
        case 0:
            ((_binding->rx_union).spawn_domain_with_caps_call).inheritcn_cap = cap;
            break;
        case 1:
            ((_binding->rx_union).spawn_domain_with_caps_call).argcn_cap = cap;
            if ((_binding->rx_msg_fragment) == 3) {
                FL_DEBUG("ump RX spawn.spawn_domain_with_caps_call\n");
                assert(((_binding->rx_vtbl).spawn_domain_with_caps_call) != NULL);
                ((_binding->rx_vtbl).spawn_domain_with_caps_call)(_binding, ((_binding->rx_union).spawn_domain_with_caps_call).path, ((_binding->rx_union).spawn_domain_with_caps_call).argvbuf, ((_binding->rx_union).spawn_domain_with_caps_call).argvbytes, ((_binding->rx_union).spawn_domain_with_caps_call).envbuf, ((_binding->rx_union).spawn_domain_with_caps_call).envbytes, ((_binding->rx_union).spawn_domain_with_caps_call).inheritcn_cap, ((_binding->rx_union).spawn_domain_with_caps_call).argcn_cap);
                _binding->rx_msgnum = 0;
                // register for receive notification
                err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = spawn_ump_rx_handler,  .arg = _binding });
                if (err_is_fail(err)) {
                    (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
                }
            }
            break;
        default:
            assert(!("invalid cap number"));
            (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
        }
        break;
    default:
        assert(!("invalid message number"));
        (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
    }
}


/*
 * Monitor mutex acquire continuation
 */
static  void spawn_ump_monitor_mutex_cont(void *arg)
{
    struct spawn_ump_binding *b = arg;
    assert(!(((b->ump_state).capst).monitor_mutex_held));
    ((b->ump_state).capst).monitor_mutex_held = true;
    if (((b->ump_state).capst).rx_cap_ack) {
        spawn_ump_cap_send_handler(b);
    }
}


/*
 * Message sender functions
 */
static  errval_t spawn_spawn_domain_call__ump_send(struct spawn_binding *_binding, struct event_closure _continuation, const char *path, const char *argvbuf, size_t argvbytes, const char *envbuf, size_t envbytes)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = spawn_spawn_domain_call__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).spawn_domain_call).path = ((char *)(path));
    ((_binding->tx_union).spawn_domain_call).argvbuf = ((char *)(argvbuf));
    ((_binding->tx_union).spawn_domain_call).argvbytes = argvbytes;
    ((_binding->tx_union).spawn_domain_call).envbuf = ((char *)(envbuf));
    ((_binding->tx_union).spawn_domain_call).envbytes = envbytes;
    FL_DEBUG("ump TX spawn.spawn_domain_call\n");
    
    // try to send!
    spawn_ump_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

