static  errval_t pci_init_pci_device_call__ump_send(struct pci_binding *_binding, struct event_closure _continuation, uint32_t class_code, uint32_t sub_class, uint32_t prog_if, uint32_t vendor_id, uint32_t device_id, uint32_t bus, uint32_t dev, uint32_t fun, uint8_t coreid, uint32_t vector)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = pci_init_pci_device_call__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).init_pci_device_call).class_code = class_code;
    ((_binding->tx_union).init_pci_device_call).sub_class = sub_class;
    ((_binding->tx_union).init_pci_device_call).prog_if = prog_if;
    ((_binding->tx_union).init_pci_device_call).vendor_id = vendor_id;
    ((_binding->tx_union).init_pci_device_call).device_id = device_id;
    ((_binding->tx_union).init_pci_device_call).bus = bus;
    ((_binding->tx_union).init_pci_device_call).dev = dev;
    ((_binding->tx_union).init_pci_device_call).fun = fun;
    ((_binding->tx_union).init_pci_device_call).coreid = coreid;
    ((_binding->tx_union).init_pci_device_call).vector = vector;
    FL_DEBUG("ump TX pci.init_pci_device_call\n");
    
    // try to send!
    pci_ump_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

