static  errval_t rcce_bulk_message_request__lmp_send(struct rcce_binding *_binding, struct event_closure _continuation, uint16_t coreid, uint64_t id, uint64_t size, uint8_t last_fragment)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = rcce_bulk_message_request__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).bulk_message_request).coreid = coreid;
    ((_binding->tx_union).bulk_message_request).id = id;
    ((_binding->tx_union).bulk_message_request).size = size;
    ((_binding->tx_union).bulk_message_request).last_fragment = last_fragment;
    FL_DEBUG("lmp TX rcce.bulk_message_request\n");
    
    // try to send!
    rcce_bulk_message_request__lmp_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

