/*
 * Receive handler
 */
 void rcce_lmp_rx_handler(void *arg)
{
    // Get the binding state from our argument pointer
    struct rcce_binding *_binding = arg;
    struct rcce_lmp_binding *b = arg;
    errval_t err;
    
    struct lmp_recv_msg msg = LMP_RECV_MSG_INIT;
    struct capref cap;
    struct event_closure recv_closure = (struct event_closure){  .handler = rcce_lmp_rx_handler,  .arg = arg };
    
    do {
        // try to retrieve a message from the channel
        err = lmp_chan_recv(&(b->chan), &msg, &cap);
        // check if we succeeded
        if (err_is_fail(err)) {
            if (err_no(err) == LIB_ERR_NO_LMP_MSG) {
                // no message
                break;
            } else {
                // real error
                (_binding->error_handler)(_binding, err_push(err, LIB_ERR_LMP_CHAN_RECV));
                return;
            }
        }
        
        // allocate a new receive slot if needed
        if (!capref_is_null(cap)) {
            err = lmp_chan_alloc_recv_slot(&(b->chan));
            if (err_is_fail(err)) {
                (_binding->error_handler)(_binding, err_push(err, LIB_ERR_LMP_ALLOC_RECV_SLOT));
            }
        }
        
        // is this the start of a new message?
        if ((_binding->rx_msgnum) == 0) {
            // check message length
            if (((msg.buf).msglen) == 0) {
                (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_EMPTY_MSG);
                break;
            }
            // unmarshall message number from first word, set fragment to 0
            _binding->rx_msgnum = (((msg.words)[0]) & 0xffff);
            _binding->rx_msg_fragment = 0;
        }
        
        // switch on message number and fragment number
        switch (_binding->rx_msgnum) {
        case rcce_init_request__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).init_request).coreid = ((((msg.words)[0]) >> 16) & 0xff);
                ((_binding->rx_union).init_request).bspid = ((((msg.words)[0]) >> 24) & 0xff);
                ((_binding->rx_union).init_request).state = ((msg.words)[1]);
                ((_binding->rx_union).init_request).shared_mem = cap;
                
                FL_DEBUG("lmp RX rcce.init_request\n");
                assert(((_binding->rx_vtbl).init_request) != NULL);
                ((_binding->rx_vtbl).init_request)(_binding, ((_binding->rx_union).init_request).coreid, ((_binding->rx_union).init_request).bspid, ((_binding->rx_union).init_request).state, ((_binding->rx_union).init_request).shared_mem);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_ssf__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).ssf).id = ((((msg.words)[0]) >> 16) & 0xff);
                ((_binding->rx_union).ssf).pyaload = ((msg.words)[1]);
                ((_binding->rx_union).ssf).timestamp = ((msg.words)[2]);
                
                FL_DEBUG("lmp RX rcce.ssf\n");
                assert(((_binding->rx_vtbl).ssf) != NULL);
                ((_binding->rx_vtbl).ssf)(_binding, ((_binding->rx_union).ssf).id, ((_binding->rx_union).ssf).pyaload, ((_binding->rx_union).ssf).timestamp);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_jump__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).jump).id = ((((msg.words)[0]) >> 16) & 0xff);
                ((_binding->rx_union).jump).timestamp = ((msg.words)[1]);
                
                FL_DEBUG("lmp RX rcce.jump\n");
                assert(((_binding->rx_vtbl).jump) != NULL);
                ((_binding->rx_vtbl).jump)(_binding, ((_binding->rx_union).jump).id, ((_binding->rx_union).jump).timestamp);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_acknowledgement__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).acknowledgement).id = ((((msg.words)[0]) >> 16) & 0xff);
                ((_binding->rx_union).acknowledgement).time = ((msg.words)[1]);
                
                FL_DEBUG("lmp RX rcce.acknowledgement\n");
                assert(((_binding->rx_vtbl).acknowledgement) != NULL);
                ((_binding->rx_vtbl).acknowledgement)(_binding, ((_binding->rx_union).acknowledgement).id, ((_binding->rx_union).acknowledgement).time);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_wait_request__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX rcce.wait_request\n");
                assert(((_binding->rx_vtbl).wait_request) != NULL);
                ((_binding->rx_vtbl).wait_request)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_wait_reply__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX rcce.wait_reply\n");
                assert(((_binding->rx_vtbl).wait_reply) != NULL);
                ((_binding->rx_vtbl).wait_reply)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_ring_request__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX rcce.ring_request\n");
                assert(((_binding->rx_vtbl).ring_request) != NULL);
                ((_binding->rx_vtbl).ring_request)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_ring_reply__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX rcce.ring_reply\n");
                assert(((_binding->rx_vtbl).ring_reply) != NULL);
                ((_binding->rx_vtbl).ring_reply)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_error_reply__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).error_reply).err = ((msg.words)[1]);
                ((_binding->rx_union).error_reply).state = ((msg.words)[2]);
                
                FL_DEBUG("lmp RX rcce.error_reply\n");
                assert(((_binding->rx_vtbl).error_reply) != NULL);
                ((_binding->rx_vtbl).error_reply)(_binding, ((_binding->rx_union).error_reply).err, ((_binding->rx_union).error_reply).state);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_message_request__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).message_request).coreid = ((((msg.words)[0]) >> 16) & 0xffff);
                
                (_binding->rx_msg_fragment)++;
                break;
            case 1:
                err = flounder_stub_lmp_recv_buf(&msg, (void **)(&(((_binding->rx_union).message_request).msg)), &(((_binding->rx_union).message_request).size), &(_binding->rx_str_pos));
                if (err_is_ok(err)) {
                    FL_DEBUG("lmp RX rcce.message_request\n");
                    assert(((_binding->rx_vtbl).message_request) != NULL);
                    ((_binding->rx_vtbl).message_request)(_binding, ((_binding->rx_union).message_request).coreid, ((_binding->rx_union).message_request).msg, ((_binding->rx_union).message_request).size);
                    _binding->rx_msgnum = 0;
                } else {
                    if (err_no(err) != FLOUNDER_ERR_BUF_RECV_MORE) {
                        (_binding->error_handler)(_binding, err);
                    }
                }
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_message_reply__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).message_reply).coreid = ((((msg.words)[0]) >> 16) & 0xffff);
                
                FL_DEBUG("lmp RX rcce.message_reply\n");
                assert(((_binding->rx_vtbl).message_reply) != NULL);
                ((_binding->rx_vtbl).message_reply)(_binding, ((_binding->rx_union).message_reply).coreid);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_bulk_message_request__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).bulk_message_request).last_fragment = ((((msg.words)[0]) >> 16) & 0xff);
                ((_binding->rx_union).bulk_message_request).coreid = ((((msg.words)[0]) >> 24) & 0xffff);
                ((_binding->rx_union).bulk_message_request).id = ((msg.words)[1]);
                ((_binding->rx_union).bulk_message_request).size = ((msg.words)[2]);
                
                FL_DEBUG("lmp RX rcce.bulk_message_request\n");
                assert(((_binding->rx_vtbl).bulk_message_request) != NULL);
                ((_binding->rx_vtbl).bulk_message_request)(_binding, ((_binding->rx_union).bulk_message_request).coreid, ((_binding->rx_union).bulk_message_request).id, ((_binding->rx_union).bulk_message_request).size, ((_binding->rx_union).bulk_message_request).last_fragment);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_bulk_message_reply__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).bulk_message_reply).coreid = ((((msg.words)[0]) >> 16) & 0xffff);
                ((_binding->rx_union).bulk_message_reply).id = ((msg.words)[1]);
                
                FL_DEBUG("lmp RX rcce.bulk_message_reply\n");
                assert(((_binding->rx_vtbl).bulk_message_reply) != NULL);
                ((_binding->rx_vtbl).bulk_message_reply)(_binding, ((_binding->rx_union).bulk_message_reply).coreid, ((_binding->rx_union).bulk_message_reply).id);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case rcce_bulk_recv_ready__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).bulk_recv_ready).coreid = ((((msg.words)[0]) >> 16) & 0xffff);
                ((_binding->rx_union).bulk_recv_ready).size = ((msg.words)[1]);
                
                FL_DEBUG("lmp RX rcce.bulk_recv_ready\n");
                assert(((_binding->rx_vtbl).bulk_recv_ready) != NULL);
                ((_binding->rx_vtbl).bulk_recv_ready)(_binding, ((_binding->rx_union).bulk_recv_ready).coreid, ((_binding->rx_union).bulk_recv_ready).size);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        default:
            (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_MSGNUM);
            goto out;
        }
    } while (err_is_ok(err));
    out:
    // re-register for another receive notification
    err = lmp_chan_register_recv(&(b->chan), _binding->waitset, recv_closure);
    assert(err_is_ok(err));
}


/*
 * Control functions
 */
static  bool rcce_lmp_can_send(struct rcce_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t rcce_lmp_register_send(struct rcce_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, rcce_lmp_can_send(b)));
}

static  void rcce_lmp_default_error_handler(struct rcce_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated rcce lmp binding (default handler)");
    abort();
}

static  errval_t rcce_lmp_change_waitset(struct rcce_binding *_binding, struct waitset *ws)
{
    struct rcce_lmp_binding *b = (void *)(_binding);
    
    // Migrate register and TX continuation notifications
    flounder_support_migrate_notify(&(_binding->register_chanstate), ws);
    flounder_support_migrate_notify(&(_binding->tx_cont_chanstate), ws);
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // Migrate send and receive notifications
    lmp_chan_migrate_recv(&(b->chan), ws);
    lmp_chan_migrate_send(&(b->chan), ws);
    
    return(SYS_ERR_OK);
}

static  errval_t rcce_lmp_control(struct rcce_binding *_binding, idc_control_t control)
{
    struct rcce_lmp_binding *b = (void *)(_binding);
    
    b->flags = idc_control_to_lmp_flags(control, b->flags);
    
    return(SYS_ERR_OK);
}

/*
 * Functions to initialise/destroy the binding state
 */
 void rcce_lmp_init(struct rcce_lmp_binding *b, struct waitset *waitset)
{
    (b->b).st = NULL;
    (b->b).waitset = waitset;
    event_mutex_init(&((b->b).mutex), waitset);
    (b->b).can_send = rcce_lmp_can_send;
    (b->b).register_send = rcce_lmp_register_send;
    (b->b).error_handler = rcce_lmp_default_error_handler;
    (b->b).tx_vtbl = rcce_lmp_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    lmp_chan_init(&(b->chan));
    (b->b).change_waitset = rcce_lmp_change_waitset;
    (b->b).control = rcce_lmp_control;
    b->flags = LMP_SEND_FLAGS_DEFAULT;
}

 void rcce_lmp_destroy(struct rcce_lmp_binding *b)
{
    flounder_support_waitset_chanstate_destroy(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_destroy(&((b->b).tx_cont_chanstate));
    lmp_chan_destroy(&(b->chan));
}


/*
 * Bind function
 */
static  void rcce_lmp_bind_continuation(void *st, errval_t err, struct lmp_chan *chan)
{
    struct rcce_lmp_binding *b = st;
    
    if (err_is_ok(err)) {
        // allocate a cap receive slot
        err = lmp_chan_alloc_recv_slot(chan);
        if (err_is_fail(err)) {
            err = err_push(err, LIB_ERR_LMP_ALLOC_RECV_SLOT);
            goto fail;
        }
        
        // register for receive
        err = lmp_chan_register_recv(chan, (b->b).waitset, (struct event_closure){  .handler = rcce_lmp_rx_handler,  .arg = b });
        if (err_is_fail(err)) {
            err = err_push(err, LIB_ERR_CHAN_REGISTER_RECV);
            goto fail;
        }
    } else {
        fail:
        rcce_lmp_destroy(b);
    }
    
    ((b->b).bind_cont)((b->b).st, err, &(b->b));
}

