static  errval_t rcce_init_request__multihop_send(struct rcce_binding *_binding, struct event_closure _continuation, uint8_t coreid, uint8_t bspid, uint64_t state, struct capref shared_mem)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and the arguments
    _binding->tx_msgnum = rcce_init_request__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).init_request).coreid = coreid;
    ((_binding->tx_union).init_request).bspid = bspid;
    ((_binding->tx_union).init_request).state = state;
    ((_binding->tx_union).init_request).shared_mem = shared_mem;
    FL_DEBUG("multihop TX rcce.init_request\n");
    
    // try to send!
    rcce_init_request__multihop_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

