static  errval_t usb_manager_connect_call__ump_send(struct usb_manager_binding *_binding, struct event_closure _continuation, iref_t driver_iref, uint16_t init_config)
{
    // check that we can accept an outgoing message
    if ((_binding->tx_msgnum) != 0) {
        return(FLOUNDER_ERR_TX_BUSY);
    }
    
    // register send continuation
    if ((_continuation.handler) != NULL) {
        errval_t _err;
        _err = flounder_support_register(_binding->waitset, &(_binding->tx_cont_chanstate), _continuation, false);
        // may fail if previous continuation hasn't fired yet
        if (err_is_fail(_err)) {
            if (err_no(_err) == LIB_ERR_CHAN_ALREADY_REGISTERED) {
                return(FLOUNDER_ERR_TX_BUSY);
            } else {
                assert(!("shouldn't happen"));
                return(_err);
            }
        }
    }
    
    // store message number and arguments
    _binding->tx_msgnum = usb_manager_connect_call__msgnum;
    _binding->tx_msg_fragment = 0;
    ((_binding->tx_union).connect_call).driver_iref = driver_iref;
    ((_binding->tx_union).connect_call).init_config = init_config;
    FL_DEBUG("ump TX usb_manager.connect_call\n");
    
    // try to send!
    usb_manager_ump_send_handler(_binding);
    
    return(SYS_ERR_OK);
}

