/*
 * Send vtable
 */
static  struct bcache_tx_vtbl bcache_lmp_tx_vtbl = {
    .new_client_call = bcache_new_client_call__lmp_send,
    .new_client_response = bcache_new_client_response__lmp_send,
    .get_start_call = bcache_get_start_call__lmp_send,
    .get_start_response = bcache_get_start_response__lmp_send,
    .get_stop_call = bcache_get_stop_call__lmp_send,
    .get_stop_response = bcache_get_stop_response__lmp_send,
    .print_stats_call = bcache_print_stats_call__lmp_send,
    .print_stats_response = bcache_print_stats_response__lmp_send,
};
/*
 * Receive handler
 */
 void bcache_lmp_rx_handler(void *arg)
{
    // Get the binding state from our argument pointer
    struct bcache_binding *_binding = arg;
    struct bcache_lmp_binding *b = arg;
    errval_t err;
    
    struct lmp_recv_msg msg = LMP_RECV_MSG_INIT;
    struct capref cap;
    struct event_closure recv_closure = (struct event_closure){  .handler = bcache_lmp_rx_handler,  .arg = arg };
    
    do {
        // try to retrieve a message from the channel
        err = lmp_chan_recv(&(b->chan), &msg, &cap);
        // check if we succeeded
        if (err_is_fail(err)) {
            if (err_no(err) == LIB_ERR_NO_LMP_MSG) {
                // no message
                break;
            } else {
                // real error
                (_binding->error_handler)(_binding, err_push(err, LIB_ERR_LMP_CHAN_RECV));
                return;
            }
        }
        
        // allocate a new receive slot if needed
        if (!capref_is_null(cap)) {
            err = lmp_chan_alloc_recv_slot(&(b->chan));
            if (err_is_fail(err)) {
                (_binding->error_handler)(_binding, err_push(err, LIB_ERR_LMP_ALLOC_RECV_SLOT));
            }
        }
        
        // is this the start of a new message?
        if ((_binding->rx_msgnum) == 0) {
            // check message length
            if (((msg.buf).msglen) == 0) {
                (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_EMPTY_MSG);
                break;
            }
            // unmarshall message number from first word, set fragment to 0
            _binding->rx_msgnum = (((msg.words)[0]) & 0xffff);
            _binding->rx_msg_fragment = 0;
        }
        
        // switch on message number and fragment number
        switch (_binding->rx_msgnum) {
        case bcache_new_client_call__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX bcache.new_client_call\n");
                assert(((_binding->rx_vtbl).new_client_call) != NULL);
                ((_binding->rx_vtbl).new_client_call)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_new_client_response__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).new_client_response).bulk = cap;
                
                FL_DEBUG("lmp RX bcache.new_client_response\n");
                assert(((_binding->rx_vtbl).new_client_response) != NULL);
                ((_binding->rx_vtbl).new_client_response)(_binding, ((_binding->rx_union).new_client_response).bulk);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_get_start_call__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                (_binding->rx_msg_fragment)++;
                break;
            case 1:
                err = flounder_stub_lmp_recv_buf(&msg, (void **)(&(((_binding->rx_union).get_start_call).key)), &(((_binding->rx_union).get_start_call).key_len), &(_binding->rx_str_pos));
                if (err_is_ok(err)) {
                    FL_DEBUG("lmp RX bcache.get_start_call\n");
                    assert(((_binding->rx_vtbl).get_start_call) != NULL);
                    ((_binding->rx_vtbl).get_start_call)(_binding, ((_binding->rx_union).get_start_call).key, ((_binding->rx_union).get_start_call).key_len);
                    _binding->rx_msgnum = 0;
                } else {
                    if (err_no(err) != FLOUNDER_ERR_BUF_RECV_MORE) {
                        (_binding->error_handler)(_binding, err);
                    }
                }
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_get_start_response__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) != 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).get_start_response).haveit = ((((msg.words)[0]) >> 16) & 0x1);
                ((_binding->rx_union).get_start_response).idx = ((msg.words)[1]);
                ((_binding->rx_union).get_start_response).transid = ((msg.words)[2]);
                ((_binding->rx_union).get_start_response).size = ((msg.words)[3]);
                
                FL_DEBUG("lmp RX bcache.get_start_response\n");
                assert(((_binding->rx_vtbl).get_start_response) != NULL);
                ((_binding->rx_vtbl).get_start_response)(_binding, ((_binding->rx_union).get_start_response).idx, ((_binding->rx_union).get_start_response).haveit, ((_binding->rx_union).get_start_response).transid, ((_binding->rx_union).get_start_response).size);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_get_stop_call__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) != 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                ((_binding->rx_union).get_stop_call).transid = ((msg.words)[1]);
                ((_binding->rx_union).get_stop_call).idx = ((msg.words)[2]);
                ((_binding->rx_union).get_stop_call).length = ((msg.words)[3]);
                
                FL_DEBUG("lmp RX bcache.get_stop_call\n");
                assert(((_binding->rx_vtbl).get_stop_call) != NULL);
                ((_binding->rx_vtbl).get_stop_call)(_binding, ((_binding->rx_union).get_stop_call).transid, ((_binding->rx_union).get_stop_call).idx, ((_binding->rx_union).get_stop_call).length);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_get_stop_response__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX bcache.get_stop_response\n");
                assert(((_binding->rx_vtbl).get_stop_response) != NULL);
                ((_binding->rx_vtbl).get_stop_response)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_print_stats_call__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX bcache.print_stats_call\n");
                assert(((_binding->rx_vtbl).print_stats_call) != NULL);
                ((_binding->rx_vtbl).print_stats_call)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        case bcache_print_stats_response__msgnum:
            switch (_binding->rx_msg_fragment) {
            case 0:
                // check length
                if (((msg.buf).msglen) > 4) {
                    (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_LENGTH);
                    goto out;
                }
                
                
                FL_DEBUG("lmp RX bcache.print_stats_response\n");
                assert(((_binding->rx_vtbl).print_stats_response) != NULL);
                ((_binding->rx_vtbl).print_stats_response)(_binding);
                _binding->rx_msgnum = 0;
                break;
            default:
                (_binding->error_handler)(_binding, FLOUNDER_ERR_INVALID_STATE);
                goto out;
            }
            break;
        default:
            (_binding->error_handler)(_binding, FLOUNDER_ERR_RX_INVALID_MSGNUM);
            goto out;
        }
    } while (err_is_ok(err));
    out:
    // re-register for another receive notification
    err = lmp_chan_register_recv(&(b->chan), _binding->waitset, recv_closure);
    assert(err_is_ok(err));
}


/*
 * Control functions
 */
static  bool bcache_lmp_can_send(struct bcache_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t bcache_lmp_register_send(struct bcache_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, bcache_lmp_can_send(b)));
}

static  void bcache_lmp_default_error_handler(struct bcache_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated bcache lmp binding (default handler)");
    abort();
}

static  errval_t bcache_lmp_change_waitset(struct bcache_binding *_binding, struct waitset *ws)
{
    struct bcache_lmp_binding *b = (void *)(_binding);
    
    // Migrate register and TX continuation notifications
    flounder_support_migrate_notify(&(_binding->register_chanstate), ws);
    flounder_support_migrate_notify(&(_binding->tx_cont_chanstate), ws);
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // Migrate send and receive notifications
    lmp_chan_migrate_recv(&(b->chan), ws);
    lmp_chan_migrate_send(&(b->chan), ws);
    
    return(SYS_ERR_OK);
}

static  errval_t bcache_lmp_control(struct bcache_binding *_binding, idc_control_t control)
{
    struct bcache_lmp_binding *b = (void *)(_binding);
    
    b->flags = idc_control_to_lmp_flags(control, b->flags);
    
    return(SYS_ERR_OK);
}

/*
 * Functions to initialise/destroy the binding state
 */
 void bcache_lmp_init(struct bcache_lmp_binding *b, struct waitset *waitset)
{
    (b->b).st = NULL;
    (b->b).waitset = waitset;
    event_mutex_init(&((b->b).mutex), waitset);
    (b->b).can_send = bcache_lmp_can_send;
    (b->b).register_send = bcache_lmp_register_send;
    (b->b).error_handler = bcache_lmp_default_error_handler;
    (b->b).tx_vtbl = bcache_lmp_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    lmp_chan_init(&(b->chan));
    (b->b).change_waitset = bcache_lmp_change_waitset;
    (b->b).control = bcache_lmp_control;
    b->flags = LMP_SEND_FLAGS_DEFAULT;
}

