/*
 * Connect callback for export
 */
 errval_t acpi_ump_connect_handler(void *st, struct monitor_binding *mb, uintptr_t mon_id, struct capref frame, size_t inchanlen, size_t outchanlen, struct capref notify_cap)
{
    struct acpi_export *e = st;
    errval_t err;
    
    // allocate storage for binding
    struct acpi_ump_binding *b = malloc(sizeof(struct acpi_ump_binding ));
    if (b == NULL) {
        return(LIB_ERR_MALLOC_FAIL);
    }
    
    struct acpi_binding *_binding = &(b->b);
    (b->b).st = NULL;
    (b->b).waitset = (e->waitset);
    event_mutex_init(&((b->b).mutex), e->waitset);
    (b->b).can_send = acpi_ump_can_send;
    (b->b).register_send = acpi_ump_register_send;
    (b->b).error_handler = acpi_ump_default_error_handler;
    (b->b).tx_vtbl = acpi_ump_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    flounder_stub_ump_state_init(&(b->ump_state), b);
    (b->b).change_waitset = acpi_ump_change_waitset;
    (b->b).control = acpi_ump_control;
    
    // run user's connect handler
    err = ((e->connect_cb)(e->st, _binding));
    if (err_is_fail(err)) {
        // connection refused
        acpi_ump_destroy(b);
        return(err);
    }
    
    // accept the connection and setup the channel
    err = ump_chan_accept(&((b->ump_state).chan), mon_id, frame, inchanlen, outchanlen);
    if (err_is_fail(err)) {
        err = err_push(err, LIB_ERR_UMP_CHAN_ACCEPT);
        (_binding->error_handler)(_binding, err);
        return(err);
    }
    
    // notify cap ignored
    // setup cap handlers
    (((b->ump_state).chan).cap_handlers).st = b;
    (((b->ump_state).chan).cap_handlers).cap_receive_handler = acpi_ump_cap_rx_handler;
    
    // register for receive notification
    err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = acpi_ump_rx_handler,  .arg = _binding });
    if (err_is_fail(err)) {
        (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
    }
    
    // send back bind reply
    ump_chan_send_bind_reply(mb, &((b->ump_state).chan), SYS_ERR_OK, mon_id, NULL_CAP);
    
    return(SYS_ERR_OK);
}

