/*
 * Control functions
 */
static  bool acpi_multihop_can_send(struct acpi_binding *b)
{
    struct acpi_multihop_binding *mb = (struct acpi_multihop_binding *)(b);
    return(((b->tx_msgnum) == 0) && (!multihop_chan_is_window_full(&(mb->chan))));
}

static  errval_t acpi_multihop_register_send(struct acpi_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, acpi_multihop_can_send(b)));
}

static  void acpi_multihop_default_error_handler(struct acpi_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated acpi multihop binding (default handler)");
    abort();
}

static  errval_t acpi_multihop_change_waitset(struct acpi_binding *_binding, struct waitset *ws)
{
    struct acpi_multihop_binding *mb = (void *)(_binding);
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // change waitset on multi-hop channel
    return(multihop_chan_change_waitset(&(mb->chan), ws));
}

static  errval_t acpi_multihop_control(struct acpi_binding *_binding, idc_control_t control)
{
    // No control flags supported
    return(SYS_ERR_OK);
}

/*
 * Functions to initialise/destroy the binding state
 */
