/*
 * Control functions
 */
static  bool trivfs_lmp_can_send(struct trivfs_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t trivfs_lmp_register_send(struct trivfs_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, trivfs_lmp_can_send(b)));
}

static  void trivfs_lmp_default_error_handler(struct trivfs_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated trivfs lmp binding (default handler)");
    abort();
}

static  errval_t trivfs_lmp_change_waitset(struct trivfs_binding *_binding, struct waitset *ws)
{
    struct trivfs_lmp_binding *b = (void *)(_binding);
    
    // Migrate register and TX continuation notifications
    flounder_support_migrate_notify(&(_binding->register_chanstate), ws);
    flounder_support_migrate_notify(&(_binding->tx_cont_chanstate), ws);
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // Migrate send and receive notifications
    lmp_chan_migrate_recv(&(b->chan), ws);
    lmp_chan_migrate_send(&(b->chan), ws);
    
    return(SYS_ERR_OK);
}

