/*
 * Send vtable
 */
static  struct test_tx_vtbl test_ump_tx_vtbl = {
    .basic = test_basic__ump_send,
    .str = test_str__ump_send,
    .caps = test_caps__ump_send,
    .buf = test_buf__ump_send,
};
/*
 * Control functions
 */
static  bool test_ump_can_send(struct test_binding *b)
{
    return((b->tx_msgnum) == 0);
}

static  errval_t test_ump_register_send(struct test_binding *b, struct waitset *ws, struct event_closure _continuation)
{
    return(flounder_support_register(ws, &(b->register_chanstate), _continuation, test_ump_can_send(b)));
}

static  void test_ump_default_error_handler(struct test_binding *b, errval_t err)
{
    DEBUG_ERR(err, "asynchronous error in Flounder-generated test ump binding (default handler)");
    abort();
}

static  errval_t test_ump_change_waitset(struct test_binding *_binding, struct waitset *ws)
{
    struct test_ump_binding *b = (void *)(_binding);
    errval_t err;
    
    // change waitset on private monitor binding if we have one
    if ((((b->ump_state).chan).monitor_binding) != get_monitor_binding()) {
        err = flounder_support_change_monitor_waitset(((b->ump_state).chan).monitor_binding, ws);
        if (err_is_fail(err)) {
            return(err_push(err, FLOUNDER_ERR_CHANGE_MONITOR_WAITSET));
        }
    }
    
    // change waitset on binding
    _binding->waitset = ws;
    
    // re-register for receive (if previously registered)
    err = ump_chan_deregister_recv(&((b->ump_state).chan));
    if (err_is_fail(err) && (err_no(err) != LIB_ERR_CHAN_NOT_REGISTERED)) {
        return(err_push(err, LIB_ERR_CHAN_DEREGISTER_RECV));
    }
    if (err_is_ok(err)) {
        err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = test_ump_rx_handler,  .arg = _binding });
        if (err_is_fail(err)) {
            return(err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
        }
    }
    return(SYS_ERR_OK);
}

static  errval_t test_ump_control(struct test_binding *_binding, idc_control_t control)
{
    // no control flags are supported
    return(SYS_ERR_OK);
}


/*
 * Function to destroy the binding state
 */
 void test_ump_destroy(struct test_ump_binding *b)
{
    flounder_support_waitset_chanstate_destroy(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_destroy(&((b->b).tx_cont_chanstate));
    ump_chan_destroy(&((b->ump_state).chan));
}


/*
 * Bind function
 */
static  void test_ump_bind_continuation(void *st, errval_t err, struct ump_chan *chan, struct capref notify_cap)
{
    struct test_binding *_binding = st;
    struct test_ump_binding *b = st;
    
    if (err_is_ok(err)) {
        // notify cap ignored
        // setup cap handlers
        (((b->ump_state).chan).cap_handlers).st = b;
        (((b->ump_state).chan).cap_handlers).cap_receive_handler = test_ump_cap_rx_handler;
        // register for receive notification
        err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = test_ump_rx_handler,  .arg = _binding });
        if (err_is_fail(err)) {
            (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
        }
    } else {
        test_ump_destroy(b);
    }
    
    (_binding->bind_cont)(_binding->st, err, _binding);
}

 errval_t test_ump_init(struct test_ump_binding *b, struct waitset *waitset, volatile void *inbuf, size_t inbufsize, volatile void *outbuf, size_t outbufsize)
{
    errval_t err;
    struct test_binding *_binding = &(b->b);
    (b->b).st = NULL;
    (b->b).waitset = waitset;
    event_mutex_init(&((b->b).mutex), waitset);
    (b->b).can_send = test_ump_can_send;
    (b->b).register_send = test_ump_register_send;
    (b->b).error_handler = test_ump_default_error_handler;
    (b->b).tx_vtbl = test_ump_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    flounder_stub_ump_state_init(&(b->ump_state), b);
    err = ump_chan_init(&((b->ump_state).chan), inbuf, inbufsize, outbuf, outbufsize);
    if (err_is_fail(err)) {
        test_ump_destroy(b);
        return(err_push(err, LIB_ERR_UMP_CHAN_INIT));
    }
    
    (b->b).change_waitset = test_ump_change_waitset;
    (b->b).control = test_ump_control;
    // register for receive notification
    err = ump_chan_register_recv(&((b->ump_state).chan), _binding->waitset, (struct event_closure){  .handler = test_ump_rx_handler,  .arg = _binding });
    if (err_is_fail(err)) {
        (_binding->error_handler)(_binding, err_push(err, LIB_ERR_CHAN_REGISTER_RECV));
    }
    
    return(err);
}

static  void test_ump_new_monitor_binding_continuation(void *st, errval_t err, struct monitor_binding *monitor_binding)
{
    struct test_binding *_binding = st;
    struct test_ump_binding *b = st;
    
    if (err_is_fail(err)) {
        err = err_push(err, LIB_ERR_MONITOR_CLIENT_BIND);
        goto out;
    }
    
    ((b->ump_state).chan).monitor_binding = monitor_binding;
    // start the bind on the new monitor binding
    err = ump_chan_bind(&((b->ump_state).chan), (struct ump_bind_continuation){  .handler = test_ump_bind_continuation,  .st = b }, &(_binding->event_qnode), b->iref, monitor_binding, b->inchanlen, b->outchanlen, NULL_CAP);
    
    out:
    if (err_is_fail(err)) {
        (_binding->bind_cont)(_binding->st, err, _binding);
        test_ump_destroy(b);
    }
}

 errval_t test_ump_bind(struct test_ump_binding *b, iref_t iref, test_bind_continuation_fn *_continuation, void *st, struct waitset *waitset, idc_bind_flags_t flags, size_t inchanlen, size_t outchanlen)
{
    errval_t err;
    (b->b).st = NULL;
    (b->b).waitset = waitset;
    event_mutex_init(&((b->b).mutex), waitset);
    (b->b).can_send = test_ump_can_send;
    (b->b).register_send = test_ump_register_send;
    (b->b).error_handler = test_ump_default_error_handler;
    (b->b).tx_vtbl = test_ump_tx_vtbl;
    memset(&((b->b).rx_vtbl), 0, sizeof((b->b).rx_vtbl));
    flounder_support_waitset_chanstate_init(&((b->b).register_chanstate));
    flounder_support_waitset_chanstate_init(&((b->b).tx_cont_chanstate));
    (b->b).tx_msgnum = 0;
    (b->b).rx_msgnum = 0;
    (b->b).tx_msg_fragment = 0;
    (b->b).rx_msg_fragment = 0;
    (b->b).tx_str_pos = 0;
    (b->b).rx_str_pos = 0;
    (b->b).tx_str_len = 0;
    (b->b).rx_str_len = 0;
    (b->b).bind_cont = NULL;
    flounder_stub_ump_state_init(&(b->ump_state), b);
    (b->b).change_waitset = test_ump_change_waitset;
    (b->b).control = test_ump_control;
    (b->b).st = st;
    (b->b).bind_cont = _continuation;
    b->iref = iref;
    b->inchanlen = inchanlen;
    b->outchanlen = outchanlen;
    
    // do we need a new monitor binding?
    if (flags & IDC_BIND_FLAG_RPC_CAP_TRANSFER) {
        err = monitor_client_new_binding(test_ump_new_monitor_binding_continuation, b, waitset, DEFAULT_LMP_BUF_WORDS);
    } else {
        err = ump_chan_bind(&((b->ump_state).chan), (struct ump_bind_continuation){  .handler = test_ump_bind_continuation,  .st = b }, &((b->b).event_qnode), iref, get_monitor_binding(), inchanlen, outchanlen, NULL_CAP);
    }
    
    if (err_is_fail(err)) {
        test_ump_destroy(b);
    }
    return(err);
}


